/**
 * Form field validation plugin
 */
$$.fjs.validate = {
    /**
     * List of required plugins
     */
    requires: ['forms'],
    /**
     * Plugin registration handler
     */
    register: function() {
        //Stores field errors appeared during validation
        this.fieldErrors = {};
    },
    /**
     * Registers validator definitions
     * 
     * @param {String}   name            Validator name
     * @param {function} definition      Validator runtime funtion
     * @param {Object}   [defaultConfig] Vaidator default configuration parameters
     * 
     * @return {Object] $$.fjs.validate
     */
    add: function(name, definition, defaultConfig) {
        defaultConfig = $$.extend({}, defaultConfig, {
            error_message: ''
        });
        $$.fjs.validate[name] = {
            'func':definition, 
            'config':defaultConfig
        }
        $$.fjs.log('Registered validator "'+name+'"');
        return this;
    },
    /**
     * Validates form field
     * 
     * @param {Object} $input jQuery object matching :input selector
     * 
     * @return {boolean}       Field validation result
     */
    field: function($input) {
        //Check if the field is NOT required and empty
        if (!$input.attr('data-fjs-required') && $$.trim($input.val()).length == 0)
            return true;
        
        //Check if the field is REQUIRED and empty
        if ($input.attr('data-fjs-required')) {
            var isOk = true;
            if ($input.is(':checkbox')) {
                isOk = $input.is(':checked');
            } else {
                isOk = $$.trim($input.val()).length != 0;
            }
            if (!isOk) {
                this.fieldErrors[$input.attr('name')] = 'This field is required';
                return false;
            }
        }
        
        //Do other validation
        if (!$input.attr('data-fjs-validator')) {
            return true;
        }
        var fieldValidationResult = true;
        //Getting the list of validators
        var validators = $input.attr('data-fjs-validator').split(',');
        //Go over all assigned validators
        for (var i=0,c=validators.length; i<c; i++) {
            var validatorName = validators[i];
            var validatorInfo = this[validatorName];
            //If we have no registered validator
            if (!validatorInfo) {
                if ($$.fjs.config.debugMode)
                    $$.fjs.warn('Validator "'+validatorName+'" is not defined');
                
                this.fieldErrors[$input.attr('name')] = 'Validator does not exist';
                fieldValidationResult = false;
                return fieldValidationResult;
            }
            var validatorConfig = validatorInfo.config;
            //Go throught the config given and extract appropriate options from data-fjs-validator_option attributes
            $$.each(validatorInfo.config, function(pName){
                var paramName = 'data-fjs-'+validatorName+'_'+pName;
                if ($input.attr(paramName)) {
                    validatorConfig[pName] = $input.attr(paramName);
                }
            });
            //Do validation (call validation func)
            var validatorResult = validatorInfo.func($input.val(), validatorConfig);
            if (!validatorResult) {
                this.fieldErrors[$input.attr('name')] = validatorConfig.error_message ? validatorConfig.error_message : validatorName;
            }
            fieldValidationResult = fieldValidationResult && validatorResult;
            if (!fieldValidationResult)
                return fieldValidationResult; //We show only one error at once
        }
        return fieldValidationResult;
    },
    /**
     * Returns associated field validation error
     * 
     * @param {Object} $input jQuery object matching :input selector
     * 
     * @return {String}       Error message, associated with given field.
     */
    fieldError: function($input) {
        return this.fieldErrors[$input.attr('name')];
    }
}

// -- DEFAULT VALIDATORS REGISTER --

//Int validator
$$.fjs.validate.add('int', function(value) {
    return parseInt(value) == value;
});

//Float validator
$$.fjs.validate.add('float', function(value) {
    return parseFloat(value) == value;
});

//Regular expressions based validator
$$.fjs.validate.add('regexp', function(value, params) {
    return new RegExp(params.regex).test(value);
},
{
    regex: /.*/
});

